import React, { useState } from 'react';
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm = ({ setUsername }) => {
  const [username, setUsernameInput] = useState('');
  const [password, setPassword] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const navigate = useNavigate(); // For navigation

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Logging in with:', { username, password }); // Log user input

    try {
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        username, // Use username instead of email
        password,
      });
      setSuccess('Login successful!');
      setError('');

      // Save token and username
      localStorage.setItem('token', response.data.token);
      setUsername(response.data.username);

      // Redirect to homepage after login
      navigate('/'); 
    } catch (error) {
      setError(error.response?.data?.message || 'Something went wrong');
      setSuccess('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col">
      <div className="mb-4">
        <label className="block text-left text-gray-700">Username:</label>
        <input
          type="text" // Changed to text for username
          value={username}
          onChange={(e) => setUsernameInput(e.target.value)}
          required
          className="w-full px-4 py-2 border rounded-lg"
        />
      </div>
      <div className="mb-4">
        <label className="block text-left text-gray-700">Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)}
          required
          className="w-full px-4 py-2 border rounded-lg"
        />
      </div>
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded-lg">
        Login
      </button>
      {error && <p className="mt-4 text-red-500">{error}</p>}
      {success && <p className="mt-4 text-green-500">{success}</p>}
    </form>
  );
};

export default LoginForm;
