import React, { useCallback, useEffect, useState } from 'react';
import { GoogleMap, useJsApiLoader, Marker } from '@react-google-maps/api';
import GPSLiveLocation from '../components/GPSLiveLocation'; // Adjusted import path

const containerStyle = {
  width: '100%',
  height: '750px',
  border: '0',
  marginTop: '20px',
};

const center = {
  lat: 18.4597,  // Swargate, Pune latitude
  lng: 73.8839,  // Swargate, Pune longitude
};



const GoogleMapComponent = () => {

  console.log("GoogleMapComponent>>>> ");

  const [latitude, setLatitude] = useState()
const [longitude, setLongitude] = useState()

const [centerLocation, setCenterLocation] = useState()

// const geo = navigator.geolocation
// geo.getCurrentPosition(userCoords)  
// function userCoords(position) {
//   console.log("function called....")
//   let userLatitude = position.coords.latitude
//   let userLongitude = position.coords.longitude
//   // console.log("Latitude: ", userLatitude);
//   // console.log("Longitude: ", userLongitude);
//   setLatitude(userLatitude)
//   setLongitude(userLongitude)
//   const c = {
//     lat: userLatitude,  // Swargate, Pune latitude
//     lng: userLongitude,  // Swargate, Pune longitude
//   };
//   setCenterLocation(c);
// }
const curr = null;

useEffect(()=>{
  console.log("useeffect called.....")
  const geo = navigator.geolocation
geo.getCurrentPosition((position)=>{
  console.log("function called....")
  let userLatitude = position.coords.latitude
  let userLongitude = position.coords.longitude
  // console.log("Latitude: ", userLatitude);
  // console.log("Longitude: ", userLongitude);
  setLatitude(userLatitude)
  setLongitude(userLongitude)
  const c = {
    lat: userLatitude,  // Swargate, Pune latitude
    lng: userLongitude,  // Swargate, Pune longitude
  };
  setCenterLocation(c);
  curr=centerLocation

})  
console.log("useeffect completed.....", centerLocation)
}, []);



// useEffect(() => {
//   console.log("useeffect called.....")
//   const geo = navigator.geolocation;

//   if (geo) {
//     // Watch user's position and update state when it changes
//     const watcher = geo.watchPosition(
//       (position) => {
//         setLatitude(position.coords.latitude);
//         setLongitude(position.coords.longitude);
//         const c = {
//           lat: position.coords.latitude,  // Swargate, Pune latitude
//           lng: position.coords.longitude,  // Swargate, Pune longitude
//         };
//         setCenterLocation(c);
//         console.log("got points.... ",centerLocation)
//       },
//       (error) => console.error('Error getting location:', error),
//       {
//         enableHighAccuracy: true, // Optional: improves accuracy
//         timeout: 5000,            // Optional: maximum wait time for a position
//         maximumAge: 0             // Optional: ensures you get fresh data
//       }
//     );

//     // Cleanup function to stop watching when component unmounts
//     return () => geo.clearWatch(watcher);
//   } else {
//     console.error("Geolocation is not supported by this browser.");
//   }
// }, []); 

//console.log("centerLocation>>>>>> ",centerLocation)

  const { isLoaded } = useJsApiLoader({
    googleMapsApiKey: 'AIzaSyA3E5Oa68_J4t4-4530RD0h_IA532wZn_8', // Replace with your Google Maps API Key
  });

  const [map, setMap] = useState(null);

  const onLoad = useCallback(function callback(map) {
    console.log(center,"  onload called...", centerLocation)
    const bounds = new window.google.maps.LatLngBounds(centerLocation);
    map.fitBounds(bounds);
    setMap(map);
  }, []);

  const onUnmount = useCallback(function callback(map) {
    setMap(null);
  }, []);

  console.log("isLoaded: ", isLoaded,"    centerLocation: ",centerLocation)
  return isLoaded && centerLocation ? (
    <div style={{ display: 'flex', flexDirection: 'column', alignItems: 'center' }}>
      <GoogleMap
        mapContainerStyle={containerStyle}
        center={centerLocation}
        zoom={13}
        onLoad={onLoad}
        onUnmount={onUnmount}
      >
        {/* Add a marker at Swargate, Pune */}
        <Marker position={centerLocation} />
      </GoogleMap>

      {/* Add GPSLiveLocation component with styling */}
      {/* <div className="gps-live-location" style={{ marginTop: '20px', padding: '10px', backgroundColor: 'white', boxShadow: '0 2px 5px rgba(0,0,0,0.2)', borderRadius: '5px', width: '90%' }}>
        <h3 className="text-lg font-semibold">Live GPS Location</h3>
        <GPSLiveLocation />
      </div> */}
    </div>
  ) : (
    <div>Loading Google Map...</div>
  );
};

export default GoogleMapComponent;
