import React, { useState } from 'react'; 
import axios from 'axios';
import { useNavigate } from 'react-router-dom';

const LoginForm = ({ setUsername }) => {
  const [username, setUsernameInput] = useState('');  // State for username input
  const [password, setPassword] = useState('');       // State for password input
  const [error, setError] = useState('');             // State for error messages
  const [success, setSuccess] = useState('');         // State for success messages
  const navigate = useNavigate();                     // For navigation

  const handleSubmit = async (e) => {
    e.preventDefault();
    console.log('Logging in with:', { username, password }); // Log user input for debugging

    try {
      // Send POST request to backend for authentication
      const response = await axios.post('http://localhost:5000/api/auth/login', {
        username,
        password,
      });

      // Handle successful login
      setSuccess(response.data.message); // Use the success message from the backend
      setError('');

      // Update username in parent component
      setUsername(response.data.username); 

      // Redirect to homepage after login
      navigate('/'); 
    } catch (error) {
      // Handle errors from backend response
      if (error.response && error.response.data && error.response.data.message) {
        setError(error.response.data.message); // Set backend error message
      } else {
        setError('Something went wrong. Please try again.'); // General error message
      }
      setSuccess('');
    }
  };

  return (
    <form onSubmit={handleSubmit} className="flex flex-col">
      {/* Username input field */}
      <div className="mb-4">
        <label className="block text-left text-gray-700">Username:</label>
        <input
          type="text"
          value={username}
          onChange={(e) => setUsernameInput(e.target.value)} // Update username state
          required
          className="w-full px-4 py-2 border rounded-lg"
        />
      </div>

      {/* Password input field */}
      <div className="mb-4">
        <label className="block text-left text-gray-700">Password:</label>
        <input
          type="password"
          value={password}
          onChange={(e) => setPassword(e.target.value)} // Update password state
          required
          className="w-full px-4 py-2 border rounded-lg"
        />
      </div>

      {/* Submit button */}
      <button type="submit" className="bg-blue-600 text-white px-4 py-2 rounded-lg">
        Login
      </button>

      {/* Error message */}
      {error && <p className="mt-4 text-red-500">{error}</p>}

      {/* Success message */}
      {success && <p className="mt-4 text-green-500">{success}</p>}
    </form>
  );
};

export default LoginForm;
