import React, { useState, useRef } from 'react';
import { Route, Routes } from 'react-router-dom'; // Use Routes and Route for rendering different pages
import Home from './pages/Home';
import About from './pages/About';
import Contact from './pages/Contact';
import Login from './pages/Login';
import SignUp from './pages/SignUp';
import { NavBar } from './components/NavBar';
import EmbeddedMap from './components/EmbeddedMap';

function App() {
  const [showMap, setShowMap] = useState(false);
  const showMapButtonRef = useRef(null);

  const handleToggleMap = () => {
    setShowMap((prevShowMap) => !prevShowMap);
  };

  return (
    <div className="min-h-screen flex flex-col">
      <NavBar />

      {/* Define the main routes of your app */}
      <div className="flex-grow">
        <Routes>
          <Route path="/" element={<Home showMapButtonRef={showMapButtonRef} />} />
          <Route path="/about" element={<About />} />
          <Route path="/contact" element={<Contact />} />
          <Route path="/login" element={<Login />} />
          <Route path="/signup" element={<SignUp />} />
        </Routes>
      </div>

      {/* Toggle Button for Google Map */}
      <div className="text-center my-5">
        <button
          className="bg-blue-600 text-white font-semibold px-6 py-3 rounded-lg hover:bg-blue-700"
          onClick={handleToggleMap}
          ref={showMapButtonRef}
        >
          {showMap ? 'Hide Map' : 'Show Map'}
        </button>
      </div>

      {/* Conditionally render the embedded map */}
      {showMap && (
        <div className="my-5 flex justify-center">
          <EmbeddedMap />
        </div>
      )}
    </div>
  );
}

export default App;
